import { useState, useEffect } from "react";

const Categories = () => {
  const [selectedIndex, setSelectedIndex] = useState(0);
  const categories = [
    { id: Math.random().toString(), name: "Action" },
    { id: Math.random().toString(), name: "Fantasy" },
    { id: Math.random().toString(), name: "Romance" },
  ];

  const components = [
    { component: () => <h1> Action </h1> },
    { component: () => <h1> Fantasy </h1> },
    { component: () => <h1> Romance </h1> },
  ];

  return (
    <div className="categories">
      <p className="title"> Browse by categories </p>

      <div className="list">
        {categories.map(({ name, id }, index) => (
          <p
            key={id}
            onClick={() => setSelectedIndex(index)}
            className={index === selectedIndex && "selected"}
          >
            {name}
          </p>
        ))}
      </div>
      {Category}
    </div>
  );
};

export default Categories;
