import { useState } from "react";

const Categories = () => {
  const [selectedIndex, setSelectedIndex] = useState(2);
  const categories = [
    { id: Math.random().toString(), name: "Action", index: 0 },
    { id: Math.random().toString(), name: "Fantasy", index: 1 },
    { id: Math.random().toString(), name: "Romance", index: 2 },
  ];

  const setCategory = (index) => setSelectedIndex(index);

  const Category = <p> One </p>;
  return (
    <div className="categories">
      <p className="title"> Browse by categories </p>

      <div className="list">
        {categories.map(({ name, id }, index) => (
          <p key={id} className={index === selectedIndex && "selected"}>
            {name}
          </p>
        ))}
      </div>
      {Category}
    </div>
  );
};

export default Categories;
